function resolveSearch(query) {
  const q = query.toLowerCase().trim();
  for (const [slang, medical] of Object.entries(SYNONYMS)) {
    if (q === slang.toLowerCase() || q.includes(slang.toLowerCase())) return medical.toLowerCase();
  }
  return q;
}

