// ======= TABS =======
function initTabs() {
  const tabBtns = document.querySelectorAll(".tab-btn");
  function activateTab(btn) {
    state.activeTab = btn.dataset.tab;
    tabBtns.forEach(b => { b.classList.remove("active"); b.setAttribute("aria-selected","false"); b.setAttribute("tabindex","-1"); });
    btn.classList.add("active"); btn.setAttribute("aria-selected","true"); btn.setAttribute("tabindex","0"); btn.focus();
    document.querySelectorAll(".tab-content").forEach(c => c.classList.remove("active"));
    document.getElementById(`tab-${state.activeTab}`).classList.add("active");
    if (state.activeTab === "multi") { renderMultiSymptomPanel(); renderMultiResults(); }
    if (state.activeTab === "lab") { renderLabPanel(); renderLabResults(); }
    if (state.activeTab === "composite") { renderCompositePanel(); }
    updateSidebarActive();
  }
  tabBtns.forEach(btn => {
    btn.addEventListener("click", () => activateTab(btn));
    btn.addEventListener("keydown", (e) => {
      const tabs = [...tabBtns];
      const idx = tabs.indexOf(btn);
      if (e.key === "ArrowRight" || e.key === "ArrowDown") { e.preventDefault(); activateTab(tabs[(idx + 1) % tabs.length]); }
      if (e.key === "ArrowLeft" || e.key === "ArrowUp") { e.preventDefault(); activateTab(tabs[(idx - 1 + tabs.length) % tabs.length]); }
    });
  });
}

// ======= SEARCH (BUG-01, UX-09: debounce + clear + auto-navigate) =======
function initSearch() {
  const input = document.getElementById("globalSearch");
  const hint = document.getElementById("searchHint");
  const clearBtn = document.getElementById("searchClear");
  const resultsEl = document.getElementById("searchResults");

  clearBtn.addEventListener("click", () => {
    input.value = ""; state.searchQuery = "";
    clearBtn.classList.remove("visible");
    hint.classList.remove("visible");
    resultsEl.classList.remove("visible");
    resetSidebarFilter();
    input.focus();
  });

  input.addEventListener("input", () => {
    debounce(() => {
      state.searchQuery = input.value.trim();
      const resolved = resolveSearch(state.searchQuery);
      const original = state.searchQuery.toLowerCase();

      clearBtn.classList.toggle("visible", !!state.searchQuery);

      if (resolved !== original && state.searchQuery) {
        hint.textContent = `\u{1F4A1} 搜尋「${state.searchQuery}」\u2192 顯示「${resolved}」相關結果`;
        hint.classList.add("visible");
      } else {
        hint.classList.remove("visible");
      }

      const q = resolved || original;
      if (!q) {
        resultsEl.classList.remove("visible");
        resetSidebarFilter();
        return;
      }

      // 檢查是否為疾病別名查詢
      const diseaseAlias = DISEASE_ALIASES[state.searchQuery] || DISEASE_ALIASES[state.searchQuery.toLowerCase()] || DISEASE_ALIASES[state.searchQuery.toUpperCase()];

      // BUG-01: Find matched symptoms and show dropdown
      const matched = DB.symptoms.filter(sym => {
        return sym.zhName.toLowerCase().includes(q) || sym.enName.toLowerCase().includes(q) ||
          sym.zhName.toLowerCase().includes(original) || sym.enName.toLowerCase().includes(original) ||
          sym.differentials.some(d => d.zh.toLowerCase().includes(q) || d.en.toLowerCase().includes(q) ||
            d.zh.toLowerCase().includes(original) || d.en.toLowerCase().includes(original));
      });

      // 疾病反向查詢：找出包含該疾病的所有鑑別診斷
      const diseaseResults = [];
      const searchTermLower = diseaseAlias ? diseaseAlias.toLowerCase() : original;

      DB.symptoms.forEach(sym => {
        if (sym.differentials) {
          sym.differentials.forEach(d => {
            if (d.zh.toLowerCase().includes(searchTermLower) || d.en.toLowerCase().includes(searchTermLower)) {
              diseaseResults.push({
                type: 'disease',
                disease: d,
                fromSymptom: sym,
                source: 'symptom'
              });
            }
          });
        }
      });

      LAB_DB.abnormalities.forEach(lab => {
        if (lab.differentials) {
          lab.differentials.forEach(d => {
            if (d.zh.toLowerCase().includes(searchTermLower) || d.en.toLowerCase().includes(searchTermLower)) {
              // 避免重複
              if (!diseaseResults.some(r => r.disease.en === d.en && r.source === 'lab')) {
                diseaseResults.push({
                  type: 'disease',
                  disease: d,
                  fromLab: lab,
                  source: 'lab'
                });
              }
            }
          });
        }
      });

      // Update sidebar filtering
      document.querySelectorAll(".symptom-btn").forEach(btn => {
        const id = btn.dataset.id;
        btn.style.display = matched.some(s => s.id === id) ? "" : "none";
      });

      // BUG-01: Show results - prioritize disease results if searching by alias
      if (diseaseAlias && diseaseResults.length > 0) {
        // 疾病別名查詢 - 顯示疾病結果
        state.searchFocusIdx = -1;
        const uniqueDiseases = [];
        const seen = new Set();
        diseaseResults.forEach(r => {
          const key = r.disease.en;
          if (!seen.has(key)) {
            seen.add(key);
            uniqueDiseases.push(r);
          }
        });

        hint.textContent = `🔍 搜尋「${state.searchQuery}」→ 找到 ${uniqueDiseases.length} 項疾病`;
        hint.classList.add("visible");

        resultsEl.innerHTML = uniqueDiseases.slice(0, 20).map((r, i) => {
          const d = r.disease;
          const urgencyBadge = d.urgency ? `<span class="urgency-badge urgency-${d.urgency}" style="font-size:0.7rem;">${URGENCY_LABELS[d.urgency]||''}</span>` : '';
          return `<div class="search-result-item disease-result" data-idx="${i}" data-disease-zh="${d.zh}" data-disease-en="${d.en}" role="option" style="border-left:3px solid var(--secondary);">
            <div><strong style="color:var(--secondary);">🏥 ${d.zh}</strong> ${urgencyBadge}</div>
            <div class="sr-en">${d.en}</div>
            <span class="sr-count" style="font-size:0.72rem;color:var(--text-light);">點擊查看疾病整合資訊</span>
          </div>`;
        }).join("");
        resultsEl.classList.add("visible");
        resultsEl.querySelectorAll(".disease-result").forEach(item => {
          item.addEventListener("click", () => {
            const diseaseZh = item.dataset.diseaseZh;
            // 呼叫疾病整合視圖
            renderDiseaseIntegrationView(diseaseZh);
          });
        });
      } else if (matched.length === 1 && diseaseResults.length === 0) {
        resultsEl.classList.remove("visible");
        navigateToSymptom(matched[0].id);
      } else if (matched.length > 0 || diseaseResults.length > 0) {
        state.searchFocusIdx = -1;
        let html = '';

        // 症狀結果
        if (matched.length > 0) {
          html += matched.map((s, i) => {
            const dxCount = s.differentials.length;
            return `<div class="search-result-item" data-id="${s.id}" data-idx="${i}" role="option">
              <div><strong>${s.zhName}</strong> <span class="sr-en">${s.enName}</span></div>
              <span class="sr-count">${dxCount} 項鑑別</span>
            </div>`;
          }).join("");
        }

        // 疾病結果（如果有但不是別名查詢）
        if (diseaseResults.length > 0 && !diseaseAlias) {
          const uniqueDiseases = [];
          const seen = new Set();
          diseaseResults.forEach(r => {
            const key = r.disease.en;
            if (!seen.has(key)) {
              seen.add(key);
              uniqueDiseases.push(r);
            }
          });

          if (uniqueDiseases.length > 0 && matched.length > 0) {
            html += '<div style="padding:0.4rem 0.8rem;background:var(--bg-light);font-size:0.75rem;color:var(--text-light);border-top:1px solid var(--border);">📋 相關疾病</div>';
          }

          html += uniqueDiseases.slice(0, 10).map((r, i) => {
            const d = r.disease;
            return `<div class="search-result-item disease-result" data-idx="${matched.length + i}" data-disease-zh="${d.zh}" data-disease-en="${d.en}" role="option" style="border-left:3px solid var(--secondary);background:#f8f9fa;">
              <div><strong style="color:var(--secondary);">🏥 ${d.zh}</strong></div>
              <span class="sr-count" style="font-size:0.72rem;">點擊查看疾病整合資訊</span>
            </div>`;
          }).join("");
        }

        resultsEl.innerHTML = html;
        resultsEl.classList.add("visible");

        resultsEl.querySelectorAll(".search-result-item:not(.disease-result)").forEach(item => {
          item.addEventListener("click", () => { navigateToSymptom(item.dataset.id); });
        });
        resultsEl.querySelectorAll(".disease-result").forEach(item => {
          item.addEventListener("click", () => {
            const diseaseZh = item.dataset.diseaseZh;
            // 呼叫疾病整合視圖
            renderDiseaseIntegrationView(diseaseZh);
          });
        });
      } else {
        resultsEl.innerHTML = '<div style="padding:0.8rem;color:var(--text-light);font-size:0.85rem;text-align:center;">未找到相關症狀或疾病</div>';
        resultsEl.classList.add("visible");
      }
    }, 200);
  });

  // UX-08: Keyboard nav in search results
  input.addEventListener("keydown", (e) => {
    const items = resultsEl.querySelectorAll(".search-result-item");
    if (!items.length || !resultsEl.classList.contains("visible")) {
      if (e.key === "Escape") {
        input.value = ""; state.searchQuery = "";
        clearBtn.classList.remove("visible");
        hint.classList.remove("visible");
        resultsEl.classList.remove("visible");
        resetSidebarFilter();
      }
      return;
    }
    if (e.key === "ArrowDown") {
      e.preventDefault();
      state.searchFocusIdx = Math.min(state.searchFocusIdx + 1, items.length - 1);
      items.forEach((it, i) => it.classList.toggle("focused", i === state.searchFocusIdx));
      items[state.searchFocusIdx].scrollIntoView({ block: "nearest" });
    } else if (e.key === "ArrowUp") {
      e.preventDefault();
      state.searchFocusIdx = Math.max(state.searchFocusIdx - 1, 0);
      items.forEach((it, i) => it.classList.toggle("focused", i === state.searchFocusIdx));
      items[state.searchFocusIdx].scrollIntoView({ block: "nearest" });
    } else if (e.key === "Enter" && state.searchFocusIdx >= 0) {
      e.preventDefault();
      navigateToSymptom(items[state.searchFocusIdx].dataset.id);
    } else if (e.key === "Escape") {
      resultsEl.classList.remove("visible");
      state.searchFocusIdx = -1;
    }
  });

  // Close results when clicking outside
  document.addEventListener("click", (e) => {
    if (!e.target.closest(".search-bar") && !e.target.closest(".search-results")) {
      resultsEl.classList.remove("visible");
    }
  });
}

