function runValidation() {
  console.log("=== 複合查詢系統驗證 ===");
  console.log(`測試案例總數: ${TEST_CASES.length}`);

  let passed = 0;
  let failed = 0;
  const failedCases = [];
  const details = [];

  TEST_CASES.forEach(tc => {
    // 設定測試狀態
    state.compositeSymptoms = tc.symptoms.filter(s => findSymptom(s));
    state.compositeLabs = tc.labs.filter(l => LAB_DB.abnormalities.find(x => x.id === l));

    // 執行查詢
    const results = compositeQuery();
    const top10 = results.slice(0, 10).map(r => r.en.toLowerCase());

    // 檢查是否匹配
    const detected = tc.expectedMatches.some(exp =>
      top10.some(r => r.includes(exp.toLowerCase()))
    );

    if (detected) {
      passed++;
      details.push({
        id: tc.id,
        name: tc.name,
        status: "✅ PASS",
        top3: top10.slice(0, 3)
      });
    } else {
      failed++;
      failedCases.push({
        id: tc.id,
        name: tc.name,
        expected: tc.expectedMatches,
        actualTop5: top10.slice(0, 5),
        symptoms: tc.symptoms,
        labs: tc.labs
      });
      details.push({
        id: tc.id,
        name: tc.name,
        status: "❌ FAIL",
        top3: top10.slice(0, 3),
        expected: tc.expectedMatches
      });
    }
  });

  // 清除測試狀態
  state.compositeSymptoms = [];
  state.compositeLabs = [];

  const rate = ((passed / TEST_CASES.length) * 100).toFixed(1);

  console.log("\n=== 驗證結果 ===");
  console.log(`檢出率: ${rate}%`);
  console.log(`通過: ${passed}/${TEST_CASES.length}`);
  console.log(`失敗: ${failed}/${TEST_CASES.length}`);

  if (failedCases.length > 0) {
    console.log("\n=== 失敗案例分析 ===");
    failedCases.forEach(fc => {
      console.log(`\n❌ ${fc.name} (${fc.id})`);
      console.log(`  期望: ${fc.expected.join(", ")}`);
      console.log(`  實際 Top5: ${fc.actualTop5.join(", ")}`);
      console.log(`  使用症狀: ${fc.symptoms.join(", ")}`);
      console.log(`  使用異常: ${fc.labs.join(", ")}`);
    });
  }

  console.log("\n=== 詳細結果 ===");
  console.table(details.map(d => ({
    ID: d.id,
    案例: d.name,
    狀態: d.status,
    Top1: d.top3[0] || "-",
    Top2: d.top3[1] || "-",
    Top3: d.top3[2] || "-"
  })));

  return {
    rate: rate + "%",
    passed,
    failed,
    total: TEST_CASES.length,
    failedCases,
    details
  };
}

