// 自動生成統計報告
function generateSystemStats() {
  const symCount = DB.symptoms.length;
  const labCount = LAB_DB.abnormalities.length;

  let totalDx = 0;
  let labDxCount = 0;

  DB.symptoms.forEach(sym => {
    if (sym.differentials) totalDx += sym.differentials.length;
  });

  LAB_DB.abnormalities.forEach(lab => {
    if (lab.differentials) labDxCount += lab.differentials.length;
  });

  console.log("=== 系統統計 ===");
  console.log(`症狀數量: ${symCount}`);
  console.log(`實驗室指標數量: ${labCount}`);
  console.log(`症狀-疾病關聯數: ${totalDx}`);
  console.log(`實驗室-疾病關聯數: ${labDxCount}`);
  console.log(`症狀-實驗室關聯表條目: ${Object.keys(SYMPTOM_LAB_LINKS).length}`);

  return {
    symptoms: symCount,
    labs: labCount,
    symptomDxLinks: totalDx,
    labDxLinks: labDxCount,
    symptomLabLinks: Object.keys(SYMPTOM_LAB_LINKS).length
  };
}

